import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function RecipeDetail() {
  const { id } = useParams();
  const [recipeCard, setRecipeCard] = useState(null);
  const [recipeDetails, setRecipeDetails] = useState(null);

  useEffect(() => {
    const fetchRecipeDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/recipes/details/${id}`);
        setRecipeCard(response.data);
      } catch (error) {
        console.error('Failed to fetch recipe details:', error);
      }
    };

    const fetchRecipeInformation = async () => {
      const infoResponse = await axios.get(`http://localhost:5000/api/recipes/information/${id}`);
      setRecipeDetails(infoResponse.data);
    };

    const submitRating = async () => {
      try {
        const response = await axios.post(`http://localhost:5000/api/recipes/${_id}/ratings`, {
          rating: parseInt(newRating),
          user: 'Username',  // Replace with actual user data if available
          review: newReview
        });
        // Append the new rating to the local state to update the UI immediately
        setRecipeDetails({
          ...recipeDetails,
          ratings: [...recipeDetails.ratings, response.data]
        });
        setNewRating('');
        setNewReview('');
      } catch (error) {
        console.error('Failed to post new rating:', error);
      }
    };

    fetchRecipeDetails();
    fetchRecipeInformation();
    submitRating();
  }, [id]);

  if (!recipeCard) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>Recipe Details</h1>
      {/* Assuming recipeCard contains a URL to an image of the recipe */}
      <img src={recipeCard.url} alt="Recipe Card" />

      <div dangerouslySetInnerHTML={{ __html: recipeDetails.summary }}></div>
      <p>Difficulty: {recipeDetails.veryHealthy ? 'Hard' : 'Easy'}</p>
      <p>Nutritional Information:</p>
      <ul>
        {recipeDetails.nutrition.nutrients.map(nutrient => (
          <li key={nutrient.name}>{nutrient.name}: {nutrient.amount} {nutrient.unit}</li>
        ))}
      </ul>
      <p>Shopping List:</p>
      <ul>
        {recipeDetails.extendedIngredients.map(ing => (
          <li key={ing.id}>{ing.name} (Quantity: {ing.amount} {ing.unit})</li>
        ))}
      </ul>

      <div>
        <h2>Ratings</h2>
        <ul>
          {recipeDetails.ratings.map((rating, index) => (
            <li key={index}>
              {rating.user}: {rating.rating} stars
              <p>{rating.review}</p>
            </li>
          ))}
        </ul>
        <div>
          <input type="number" value={newRating} onChange={e => setNewRating(e.target.value)} placeholder="Rate 1-5" />
          <input type="text" value={newReview} onChange={e => setNewReview(e.target.value)} placeholder="Review" />
          <button onClick={submitRating}>Submit Rating</button>
        </div>
      </div>
    </div>
  );
}

export default RecipeDetail;
