function RateRecipe() {
    const { id } = useParams();
    const [recipe, setRecipe] = useState(null);
    const [rating, setRating] = useState('');
    const [review, setReview] = useState('');

    useEffect(() => {
        const fetchRecipe = async () => {
            try {
                const { data } = await axios.get(`http://localhost:5000/api/recipes/${id}`);
                setRecipe(data);
            } catch (error) {
                console.error('Error fetching recipe:', error);
            }
        };

        fetchRecipe();
    }, [id]);

    const submitRating = async () => {
        try {
            const response = await axios.post(`http://localhost:5000/api/recipes/${id}/ratings`, {
                rating: parseInt(rating, 10),
                user: 'newuser', // Ideally, this should be dynamically set based on the logged-in user
                review
            });
            alert('Rating submitted!');
            setRating('');
            setReview('');
            // Refresh recipe data to show new ratings
            fetchRecipe();
        } catch (error) {
            console.error('Error submitting rating:', error);
            alert('Failed to submit rating');
        }
    };

    if (!recipe) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <h1>{recipe.title}</h1>
            {/* Display the rest of the recipe details */}
            <div>
                <input type="number" value={rating} onChange={e => setRating(e.target.value)} />
                <textarea value={review} onChange={e => setReview(e.target.value)} />
                <button onClick={submitRating}>Submit Rating</button>
            </div>
        </div>
    );
}
