import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function RateRecipe() {
    const { id } = useParams();
    const [recipe, setRecipe] = useState(null);
    const [rating, setRating] = useState('');
    const [review, setReview] = useState('');

    // Define fetchRecipe outside of useEffect to use it elsewhere
    const fetchRecipe = async () => {
        try {
            const { data } = await axios.get(`http://localhost:5000/api/recipes/${id}`);
            setRecipe(data);
        } catch (error) {
            console.error('Error fetching recipe:', error);
        }
    };

    useEffect(() => {
        fetchRecipe();  // Call it inside useEffect for the initial load
    }, [id]);

    const submitRating = async () => {
        try {
            await axios.post(`http://localhost:5000/api/recipes/${id}/ratings`, {
                rating: parseInt(rating, 10),
                user: 'newuser', // Replace 'newuser' with the actual user's username if available
                review
            });
            alert('Rating submitted!');
            setRating('');
            setReview('');
            fetchRecipe();  // Refresh recipe data to show new ratings
        } catch (error) {
            console.error('Error submitting rating:', error);
            alert('Failed to submit rating');
        }
    };

    if (!recipe) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <h1>{recipe.title}</h1>
            {/* Additional recipe details and existing ratings can be shown here */}
            <div>
                <input type="number" value={rating} onChange={e => setRating(e.target.value)} min="1" max="5" />
                <textarea value={review} onChange={e => setReview(e.target.value)} />
                <button onClick={submitRating}>Submit Rating</button>
            </div>
        </div>
    );
}

export default RateRecipe;
