function RecipeRatingsAndDetails() {
    const { id } = useParams();
    const [recipes, setRecipes] = useState([]);
    const [recipeDetail, setRecipeDetail] = useState(null);

    // Fetch all recipes if no ID is present
    useEffect(() => {
        if (!id) {
            axios.get('http://localhost:5000/api/recipes')
                .then(response => setRecipes(response.data))
                .catch(error => console.error('Error fetching recipes:', error));
        }
    }, [id]);

    // Fetch individual recipe details if ID is present
    useEffect(() => {
        if (id) {
            axios.get(`http://localhost:5000/api/recipes/${id}`)
                .then(response => setRecipeDetail(response.data))
                .catch(error => console.error('Error fetching recipe details:', error));
        }
    }, [id]);

    if (id && !recipeDetail) {
        return <div>Loading recipe details...</div>;
    }

    return (
        <div>
            {!id ? (
                <div>
                    <h1>All Recipes</h1>
                    <ul>
                        {recipes.map(recipe => (
                            <li key={recipe._id}>
                                <Link to={`/ratings/${recipe._id}`}>{recipe.name}</Link>
                            </li>
                        ))}
                    </ul>
                </div>
            ) : (
                <div>
                    <h1>{recipeDetail.name}</h1>
                    <img src={recipeDetail.imageUrl} alt="Recipe" />
                    <p>{recipeDetail.description}</p>
                    {/* Additional details */}
                </div>
            )}
        </div>
    );
}
