import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function RecipeDetail() {
    const { id } = useParams();
    const [recipeDetails, setRecipeDetails] = useState({ ratings: [] }); // Initialize with an empty ratings array

    useEffect(() => {
        const fetchRecipeDetails = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/api/recipes/details/${id}`);
                setRecipeDetails(response.data || { ratings: [] }); // Ensure ratings is always an array
            } catch (error) {
                console.error('Failed to fetch recipe details:', error);
                setRecipeDetails({ ratings: [] }); // Ensure ratings is always an array
            }
        };

        fetchRecipeDetails();
    }, [id]);

    if (!recipeDetails) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <h1>Recipe Details</h1>
            {/* Assuming recipeCard contains a URL to an image of the recipe */}
            {recipeDetails.ratings && recipeDetails.ratings.length > 0 ? (
                <ul>
                    {recipeDetails.ratings.map((rating, index) => (
                        <li key={index}>{rating.user}: {rating.rating} - {rating.review}</li>
                    ))}
                </ul>
            ) : (
                <p>No ratings available.</p>
            )}
        </div>
    );
}

export default RecipeDetail;
